    public void Calcular(String comp, String des, int cant, String tipo) {
        double st = 0;
        double descuento=0;
        if (comp.equals("Rutas Orenses")) {
            if (des.equals("Quito")) {
                st = 20 * cant;
            }
            if (des.equals("Cuenca")) {
                st = 15 * cant;
            }
            if (des.equals("Guayaquil")) {
                st = 30 * cant;
            }
        }
        
        if (comp.equals("Cifa")) {
            if (des.equals("Quito")) {
                st = 22 * cant;
            }
            if (des.equals("Cuenca")) {
                st = 16.50 * cant;
            }
            if (des.equals("Guayaquil")) {
                st = 33 * cant;
            }
        }
        
        if (comp.equals("Calderon")) {
            if (des.equals("Quito")) {
                st = 24 * cant;
            }
            if (des.equals("Cuenca")) {
                st = 18 * cant;
            }
            if (des.equals("Guayaquil")) {
                st = 36 * cant;
            }
        }
        
        if(cant>=5)
        {
           descuento=st*0.15;
           
           st=st-descuento;
        }
        
        if (tipo.equals("Diferencial"))
        {
            st=st+(st*0.20);
        }
        
        JOptionPane.showMessageDialog(null, "Cooperativa: "+comp+
                                                        "\nDestino: "+des+
                                                        "\nCantidad de boletos: "+cant+
                                                        "\nTipo de tipo de boleto:"+tipo+
                                                        "\nTotal: "+st+
                                                        "\nDescuento aplicado: "+descuento);
    }
