    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String comp = jComboBox1.getSelectedItem().toString();
        String dest = jComboBox2.getSelectedItem().toString();
        int cant = Integer.parseInt(jTextField1.getText());
        String tipo = jComboBox3.getSelectedItem().toString();

        Calcular(comp, dest, cant, tipo);
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

